package GGP014_valorizzazione_di_output_port_version

default allow := false

allow { count({i | regex.match("^[0-9]+\\.[0-9]+\\.[0-9]+$", input.afterState.dataProductVersion.interfaceComponents.outputPorts[i].version)}) == count(input.afterState.dataProductVersion.interfaceComponents.outputPorts)}
