package GGP005_valorizzazione_di_name

default allow := false

allow { count(trim(input.afterState.dataProductVersion.info.name, " ")) >= 5 

is_camel_case(input.afterState.dataProductVersion.info.name) }

is_camel_case(s) { lower_first_letter := regex.match("^[a-z]", s); no_spaces := regex.match("^[a-z][a-zA-Z0-9]*$", s); words_capitalized := regex.match("^[a-z]+([A-Z][a-z]*)*$", s); lower_first_letter; no_spaces; words_capitalized }
